import React, { useState } from 'react';

function App() {
  const [name, setName] = useState('');
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState('');

  const getWeather = async () => {
    if (!name.trim() || !city.trim()) {
      setError('Please enter your name and city name');
      setWeather(null);
      return;
    }

    setError('');

    try {
      const response = await fetch('/weather', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, city }),
      });

      if (!response.ok) {
        throw new Error('Failed to fetch weather data');
      }

      const data = await response.json();
      setWeather(data);
    } catch (err) {
      setError(err.message || 'Something went wrong');
      setWeather(null);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-sky-100 to-blue-200 flex items-center justify-center">
      <div
        className="relative w-full max-w-md h-[520px] rounded-3xl shadow-2xl overflow-hidden flex items-center justify-center"
        style={{
          backgroundImage: `url('ChatGPT Image Feb 5, 2026, 09_33_14 PM.png')`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
        }}
      >
        <div className="absolute inset-0 bg-white/40"></div>

        <div className="relative z-10 w-full px-8 text-center">
          <h1 className="text-2xl font-bold text-gray-800 mb-6">Weather App</h1>

          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Enter your name"
            className="w-full px-3 py-2 border rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-sky-400"
          />

          <input
            type="text"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            placeholder="Enter city name"
            className="w-full px-3 py-2 border rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-sky-400"
          />

          <button
            onClick={getWeather}
            className="w-full bg-sky-500 text-white py-2 rounded-lg hover:bg-sky-600 transition duration-200"
          >
            Get Weather
          </button>

          {weather && (
            <div className="mt-6 text-gray-700">
              <h2 className="text-xl font-semibold mb-1">{weather.city}</h2>
              <p>Temperature: {weather.temperature} Â°C</p>
              <p>Weather Code: {weather.description}</p>
              <p>Wind Speed: {weather.wind} km/h</p>
            </div>
          )}

          {error && <p className="text-red-500 mt-4 text-sm">{error}</p>}
        </div>
      </div>
    </div>
  );
}

export default App;
